    public int numOfSubarrays(int[] arr) {
        List<Integer> bre = new ArrayList();
        int tmp = 1;
        for(int i = 0; i < arr.length; i++){
            if(arr[i] % 2 == 1){
                bre.add(tmp);
                tmp = 1;
            } else {
                tmp++;
            }
        }
        bre.add(tmp);


        int ans = 0;
        for(int i = 1; i < bre.size(); i++){
            if (i % 2 == 1) {
                for(int x = 0; x < bre.size() - i; x++) {
                    ans += bre.get(x) * bre.get(x + i);
                }
                System.out.println(i+" "+ans);
            }
        }
        return ans;
    }
